import {
  Button,
  Checkbox,
  Input,
  InputGroup,
  InputRightElement,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  Text,
  useClipboard,
} from "@chakra-ui/react";
import { getInviteLink } from "api/handler/guilds";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function InviteModal({ isOpen, onClose }) {
  const { guildId } = useParams();
  const [inviteLink, setInviteLink] = useState("");
  const { hasCopied, onCopy } = useClipboard(inviteLink);
  const [isPermanent, setPermanent] = useState(false);

  useEffect(() => {
    if (isOpen) {
      async function fetchLink() {
        const { data } = await getInviteLink(guildId, isPermanent);
        if (data) {
          setInviteLink(data);
        }
      }
      fetchLink();
    }
  }, [isOpen, guildId, isPermanent]);

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent bg="brandGray.light">
        <ModalHeader textAlign="center" fontWeight="bold" pb={"0"}>
          Invite Link
        </ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <Text mb="4">
            Share this link with others to grant access to this server
          </Text>

          <Checkbox onChange={(e) => setPermanent(e.target.checked)} mb={4}>
            Make it unlimited / Never reset
          </Checkbox>

          <InputGroup>
            <Input
              bg="brandGray.dark"
              borderColor={hasCopied ? "brandGreen" : "black"}
              borderRadius="3px"
              focusBorderColor="highlight.standard"
              value={inviteLink}
              isReadOnly
            />
            <InputRightElement width="4.5rem">
              <Button
                h="1.75rem"
                size="sm"
                bg={hasCopied ? "brandGreen" : "highlight.standard"}
                color="white"
                type="submit"
                fontSize={"14px"}
                _hover={{ bg: "highlight.hover" }}
                _active={{ bg: "highlight.active" }}
                _focus={{ boxShadow: "none" }}
                onClick={onCopy}
              >
                {hasCopied ? "Copied" : "Copy"}
              </Button>
            </InputRightElement>
          </InputGroup>

          <Text my={"2"} fontSize={"12px"}>
            {isPermanent
              ? "Your invite link won't expire"
              : "Your invite link expires in 1 day and can only be used once"}
          </Text>
        </ModalBody>

        <ModalFooter bg="brandGray.dark" />
      </ModalContent>
    </Modal>
  );
}
